import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCircleArrowDown, faCaretDown, faExpand,faMessage } from '@fortawesome/free-solid-svg-icons'
import React from 'react';

let Available_area = ({ trucks,state }) => {

    console.log("trucks  : ",trucks);

    if(trucks.length==undefined ) return(<>No Trucks</>)

    let data=trucks.map((el)=>{

        let{}=el.data;

        if(state!==el.data.shipper_state[0])
        {
            return(<></>)
        }

        return(
            <tr>
                <td></td>
                <td></td>
                <td></td>
                <td></td>

            </tr>
        )

    })

    function handleChatIcon()
    {
        console.log("Chat icon handle");
    }

    return (
        <div className="Available-area">
            <div >
                <h3 className="first-row-heading"> Available Area</h3>
            </div>
            <div className="load-area-table">
                <table>
                    <tr>
                        <th>Truck</th>
                        <th>Driver</th>
                        <th>Client</th>
                        <th>Status-ETA</th>
                        <th>Truck Notes</th>
                        <th>Driver Notes</th>
                        <th>Chat</th>
                    </tr>

                    <tr>
                        {data}
                    </tr>

                    <tr>
                        <td>
                            <span>AT-222</span>
                            {/* <input type="radio" name="truck-assigned" value="AT-222"/> */}
                        </td>

                        <td>Maria Anders</td>
                        <td>Alpha Lion Trucking LLC</td>
                        <td>
                            <input type="datetime-local" name="truck-eta" value="" />
                        </td>
                        <td>
                            <textarea rows="" cols="" placeholder="no notes yet" className="truck-notes"></textarea>
                        </td>
                        <td>
                            <textarea rows="" cols="" placeholder="no notes yet" className="driver-notes"></textarea>
                        </td>
                        <td>

                            <FontAwesomeIcon icon={faMessage} color="black"  onClick={handleChatIcon} />

                        </td>
                    </tr>
                    <tr>
                        <td>Centro comercial Moctezuma</td>
                        <td>Francisco Chang</td>
                        <td>Mexico</td>
                    </tr>
                </table>
            </div>
        </div>
    )

}

export default Available_area;
